import React, { useState, useEffect } from 'react';
import './App.css'; // Assuming you have some CSS for styling

function App() {
  const [knocked, setKnocked] = useState(false);
  const [doorOpened, setDoorOpened] = useState(false);

  useEffect(() => {
    // Trigger the door knocking animation when the component mounts
    setKnocked(true);
    setTimeout(() => {
      setKnocked(false);
    }, 1000); // Change the time according to your animation duration
  }, []);

  const knockDoor = () => {
    setKnocked(true);
    setTimeout(() => {
      setKnocked(false);
      setDoorOpened(true);
    }, 1000); // Change the time according to your animation duration
  };

  return (
    <div className="container">
      <div className="door-container">
        <div className={`door ${doorOpened ? 'open' : ''}`}>
          <div className={`knocker ${knocked ? 'knocked' : ''}`} />
        </div>
      </div>
      {!doorOpened && (
        <button onClick={knockDoor} disabled={knocked} className="door-button">
          {knocked ? 'Knocking...' : 'Knock on the Door'}
        </button>
      )}
      {doorOpened && <p>The door is open!</p>}
    </div>
  );
}

export default App;
